import random

secret = random.randint(1, 10)
guess = int(input("🎯 Guess a number between 1 and 10: "))

if guess == secret:
    print("🎉 Correct! You're a genius!")
elif abs(guess - secret) == 1:
    print("🔥 So close! Just one off.")
else:
    print(f"❌ Nope! The correct number was {secret}. Try again.")

