lec5.defaultCode:
shopping_list = []

while True:
    choice = input("Choose: add / remove / sort / show / quit: ").lower()
    if choice == 'add':
        item = input("Enter item: ")
        shopping_list.append(item)
    elif choice == 'remove':
        item = input("Enter item to remove: ")
        if item in shopping_list:
            shopping_list.remove(item)
    elif choice == 'sort':
        shopping_list.sort()
    elif choice == 'show':
        print(shopping_list)
    elif choice == 'quit':
        break

import random

number = random.randint(1, 100)
while True:
    guess = int(input("Guess the number: "))
    if guess > number:
        print("Lower")
    elif guess < number:
        print("Higher")
    else:
        print("Correct! The number is", number)
        break
